import { AllDOGS, FILTROINPUT, TEMPERAMENTO } from "./action-types";

const initialState = {
    allDogs: [],
    copydogs:[],
    temperamento:[]
}

const reducer = (state= initialState, {type, payload})=>{
   // console.log("entro al reducer la informacion" + payload);
    switch( type ){
        case AllDOGS:

            return{
                ...state, allDogs:payload, 
                copydogs:payload
            }
        case TEMPERAMENTO:
            return {
                ...state, temperamento:payload
            }
        case FILTROINPUT:
            state.allDogs= state.copydogs

            const busquedaporname =state.allDogs.filter(element=> element.name.toLowerCase().includes(payload.toLowerCase()))
            return {
                ...state, 
                allDogs:busquedaporname
            }
        default:
            return {...state}
    }




}

export default reducer;
