import './Detail.css'
import { useParams, Link } from 'react-router-dom'
import {useState, useEffect} from 'react'
import axios from 'axios'

import imagennull from '../../img/ayuda/image.png'


const Detail = () => {
    const [juego, setJuego] = useState({});
    const { id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const endpoint = `http://localhost:3001/dogs/${id}`;
                const response = await axios.get(endpoint);
                const { datos } = response.data;

                setJuego(datos);
                console.log("Datas: " + JSON.stringify(datos));
            } catch (error) {
                console.error('Error al mostrar Juegos:', error);
            }
        };

        fetchData();
    }, [id]);

    const handleImage = (img1) => {
        const imageExtensions = ['.png', '.jpg', '.jpeg', '.gif'];

        const isValid = (imagen) => {
            if (!imagen) return false;
            const lowerCaseUrl = imagen.toLowerCase();
            return imageExtensions.some(ext => lowerCaseUrl.endsWith(ext));
        };

        const img1Valid = isValid(img1);

        return img1Valid ? img1 : imagennull;
    };

    return (
        <div className="containerDetail">
            <div className='izquierda'>
                <img src={handleImage(juego.url)} alt={juego.name} className='imagen_dogs' />
            </div>
            <div className='derecha'>
                <div>
                    <h1>{juego.breeds?.[0]?.name}</h1>
                    <Link to={'/home'}><div>X</div></Link>
                </div>
                <p>id: {juego.id}</p>
                <p>Weight: imperial {juego.breeds?.[0]?.weight?.imperial}
                {<p>Weight: metric{juego.breeds?.[0]?.weight?.imperial}</p>}</p>

                {/* Otros datos que quieras mostrar */}
            </div>
        </div>
    );
};
export default Detail;
