import './Form.css'
import {useEffect, useState} from 'react'
//import {useDispatch} from 'react-redux'


import validation from '../Validation/validation.js'

const Form = ()=>{
    ///const dispatch = useDispatch(); 
    const [errors, setErrors] = useState({});
    const [conname, setConname] =useState(40)
    const [altura, setAltura] =useState('')
    const [iniAltura, setIniAltura] =useState(0)
    const [finAltura, setFinAltura] =useState(0)

    useEffect(()=>{
       
        setAltura(iniAltura+'  a ' + finAltura)
   
    },[iniAltura, finAltura,altura])
    

    const [userData, setUserData]=useState({
        name:'', 
        description:'', 
        platforms:'', 
        image:'', 
        release_date:'', 
        rating:'', 
        genres:''
    });

    const handlChange = (event) =>{
       
        const { name, value } = event.target;

        if (name === 'name') {
            if (value.length<=40) {
                    setConname(40-value.length); // Actualizar conname con la nueva longitud
                }
        }



        setUserData({
            ...userData,
            [event.target.name]:event.target.value
        })

        setErrors(validation({
            ...userData,
            [event.target.name]:event.target.value
        }))

    }


    const handleGuardar =async (event)=>{

    }


    const handleLimpiar = () => {
        setUserData({
            name: '',
            height: '',
            platforms: '',
            image: '',
            release_date: '',
            rating: '',
            genres: ''
        });
        setErrors({});
    }
    
    const handlSeected = (event)=>{
        const {value, id}=event.target
                if((id==="final" && value >0)){
                    if(iniAltura>value){
                        setFinAltura(value)
                    }else{
                        setFinAltura('--')
                    }
                }
                if((id==="inicio" && value >0)){
                    if(finAltura>value){
                        setIniAltura(value)
                    }else{
                        setIniAltura('--')
                    }
                }
      
    }


    return (
    
        <div className="containerCreate">
                <div className="container_titulo"> 
                 <h1> Nuevo Dog</h1>
                </div>
              
                <form  onSubmit={handleGuardar}>
               <div className="contenedor_texto">
             
              
                    Raza: 
                    <input 
                            type="text" 
                            name="name" 
                            value={userData.name}
                            onChange={handlChange}
                            maxLength={40}
                            placeholder="Raza"
                            title='No Puede ser mayor a 40 caracteres'
                            /><label id='name_con'>{conname}</label>
                          
    
                            <br/>
                            <div>
                            {
                                                errors.name && <p className='error'>* {errors.name}</p>
                                            }
                                            </div>
                                            <br/>

                                           height: 
                                      <select className="plegablefrom" id='inicio' name='inicio' onChange={handlSeected}>
                                         <option value="No">De</option>
                                         
                                             {
                                                Array.from({ length: 150 }, (_, index) => (
                                                    <option key={index} value={index}>{index}</option>
                                                ))
                                            }
                                         </select> 
                                         <label className='labeldosgs'>a</label>   
                                           
                                           <select className="plegablefrom" id='final' name='final' onChange={handlSeected}>
                                         <option value="No">De</option>
                                         
                                             {
                                                Array.from({ length: 150 }, (_, index) => (
                                                    <option key={index} value={index}>{index}</option>
                                                ))
                                            }
                                         </select>
                                        <label id='centimetros'>{altura } cm</label>
                          
    
                            <br/>
                            
                            <div>
                            {
                                                errors.name && <p className='error'>* {errors.name}</p>
                                            }
                                            </div>
                                            <br/>

                    weight: 
                    <input 
                            type="text" 
                            name="height" 
                            value={userData.name}
                            onChange={handlChange}
                            maxLength={40}
                            placeholder="Raza"
                            title='No Puede ser mayor a 40 caracteres'
                            /><label id='name_con'>{conname}</label>
                          
    
                            <br/>
                            
                            <div>
                            {
                                                errors.name && <p className='error'>* {errors.name}</p>
                                            }
                                            </div>
                                            <br/>
                                            Life Span: 
                    <input 
                            type="text" 
                            name="height" 
                            value={userData.name}
                            onChange={handlChange}
                            maxLength={40}
                            placeholder="Raza"
                            title='No Puede ser mayor a 40 caracteres'
                            /><label id='name_con'>{conname}</label>
                          
    
                            <br/>
                            
                            <div>
                            {
                                                errors.name && <p className='error'>* {errors.name}</p>
                                            }
                                            </div>
                                            <br/>
    
                Imagen: 
                <input 
                type="text" 
                name="image" 
                value={userData.image}
                onChange={handlChange}
                placeholder="URL de Imagen"
                />
                <br/>
                <div>
                {
                                                errors.image && <p className='error'>* {errors.image}</p>
                                            }
                                            </div>
                </div>
               
               <div className="container_botones">
                <button className="boton_guardar" onClick={handleGuardar}>Enviar</button> 
                <button className="boton_verificar" onClick={handleLimpiar}>Limpiar</button> 
    
               </div>
               </form>
    
        </div>)
    }

export default Form;
