import './Form.css'
import {useState} from 'react'
//import {useDispatch} from 'react-redux'


import validation from '../Validation/validation.js'

const Form = ()=>{
    ///const dispatch = useDispatch(); 
    const [errors, setErrors] = useState({});
    const [conname, setConname] =useState(40)
    

    const [userData, setUserData]=useState({
        name:'', 
        description:'', 
        platforms:'', 
        image:'', 
        release_date:'', 
        rating:'', 
        genres:''
    });

    const handlChange = (event) =>{
       
        const { name, value } = event.target;

        // Verificar si el evento se originó en el campo "name"
        if (name === 'name' && value.length <= 40) {
            // Verificar si no es una acción de borrar
            if (!(event.keyCode === 8 || event.keyCode === 46)) {
                // Decrementar conname solo si es mayor que cero
                if (conname > 0) {
                    setConname(conname + 1);
                }
            }// Si es una acción de borrar
                
            else{
                setConname(conname - 1)
            }
        }



        setUserData({
            ...userData,
            [event.target.name]:event.target.value
        })

        setErrors(validation({
            ...userData,
            [event.target.name]:event.target.value
        }))

    }


    const handleGuardar =async (event)=>{

    }


    const handleLimpiar = () => {
        setUserData({
            name: '',
            description: '',
            platforms: '',
            image: '',
            release_date: '',
            rating: '',
            genres: ''
        });
        setErrors({});
    }
    


    return (
    
        <div className="containerCreate">
                <div className="container_titulo"> 
                 <h1> Nuevo Videojuego</h1>
                </div>
              
                <form  onSubmit={handleGuardar}>
               <div className="contenedor_texto">
             
              
                    Nombre: 
                    <input 
                            type="text" 
                            name="name" 
                            value={userData.name}
                            onChange={handlChange}
                            maxLength={40}
                            placeholder="Nombre del Viedogamer"
                            /><label id='name_con'>{conname}</label>
                          
    
                            <br/>
                            <div>
                            {
                                                errors.name && <p className='error'>* {errors.name}</p>
                                            }
                                            </div>
                                            <br/>
                    Descripción: 
                    <input 
                    type="text" 
                    name="description" 
                    value={userData.description}
                    onChange={handlChange}
                    
                    placeholder="Descriptions"
                    />
                    <div>
                       {
                                                errors.description && <p className='error'>* {errors.description}</p>
                                            }
                                            </div>
               
                  
                    <br/>
                    Fecha de lanzamiento: 
                    <input 
                    type="text" 
                    name="release_date"
                    value={userData.release_date} 
                    onChange={handlChange}
                    placeholder="Fecha Publicado (AAAA/MM/DD)"
                    />
                        <div>
                                    {
                                                errors.release_date && <p className='error'>* {errors.release_date}</p>
                                            }
                                            </div>
    
    
                    <br/>
             
                      Rating: 
                      <input 
                      type="text" 
                      name="rating" 
                      value={userData.rating}
                      onChange={handlChange}
                      placeholder="Rating"
                      />
                <br/>
                <div>
                {
                                                errors.rating && <p className='error'>* {errors.rating}</p>
                                            }
                                            </div>
                                            <br/>
                platforms: 
                <input 
                type="text" 
                name="platforms" 
                value={userData.platforms}
                onChange={handlChange}
                placeholder="Plataforms"
                />
                <br/>
                <div>
                {
                                                errors.platforms && <p className='error'>* {errors.platforms}</p>
                                            }
                                            </div>
                                            <br/>
                genres: 
                <input 
                type="text" 
                name="genres" 
                value={userData.genres}
                onChange={handlChange}
                placeholder="Genres"
                />
                <br/>
                <div>
                {
                                                errors.genres && <p className='error'>* {errors.genres}</p>
                                            }
                                            </div>
                                            <br/>
    
                Imagen: 
                <input 
                type="text" 
                name="image" 
                value={userData.image}
                onChange={handlChange}
                placeholder="URL de Imagen"
                />
                <br/>
                <div>
                {
                                                errors.image && <p className='error'>* {errors.image}</p>
                                            }
                                            </div>
                </div>
               
               <div className="container_botones">
                <button className="boton_guardar" onClick={handleGuardar}>Enviar</button> 
                <button className="boton_verificar" onClick={handleLimpiar}>Limpiar</button> 
    
               </div>
               </form>
    
        </div>)
    }

export default Form;
