import { AllDOGS, FILTROINPUT, TEMPERAMENTO, ORDERAZ, FILTROTEMPERAMENTO } from "./action-types";

const initialState = {
    allDogs: [],
    copydogs:[],
    temperamento:[]
}

const reducer = (state= initialState, {type, payload})=>{
   // console.log("entro al reducer la informacion" + payload);
    switch( type ){
        case AllDOGS:

            return{
                ...state, allDogs:payload,
                copydogs:payload
            }
        case TEMPERAMENTO:
            return {
                ...state, temperamento:payload
            }
        case FILTROINPUT:
            state.allDogs= state.copydogs

            const busquedaporname =state.allDogs.filter(element=> element.name.toLowerCase().includes(payload.toLowerCase()))
            return {
                ...state,
                allDogs:busquedaporname
            }
        case ORDERAZ:
           let orderedDogs = state.copydogs.slice();
              if (payload === 'A') {
                    orderedDogs.sort((a, b) => a.name.localeCompare(b.name));
                }
                
                else if (payload === 'B') {
                    orderedDogs.sort((a, b) => b.name.localeCompare(a.name));
                }
                console.log('esto manda el reduces ' + JSON.stringify(orderedDogs))
            return {
                ...state,
                allDogs:orderedDogs
            }
        case FILTROTEMPERAMENTO:
            let copy = state.copydogs
            const busquedaportemperamento = copy.filter(element=> element.temperament.toLowerCase().includes(payload.toLowerCase()))
        return {
            ...state,
            allDogs:busquedaportemperamento
        }

        default:
            return {...state}
    }




}

export default reducer;
